mapper.select:
SELECT
	EQUIP_TYPE,
	EQUIP_ID,
	EQUIP_NAME
FROM
	(
		SELECT 'MME' AS EQUIP_TYPE, MME_ID AS EQUIP_ID, MME_NAME AS EQUIP_NAME FROM TB_CO_MME WHERE EMS_ID = #{cnEms_id}
		UNION ALL
		SELECT 'PGW' AS EQUIP_TYPE, PGW_ID AS EQUIP_ID, PGW_NAME AS EQUIP_NAME FROM TB_CO_PGW WHERE EMS_ID = #{cnEms_id}
	) AS EQUIP

mapper.update:
UPDATE TB_CO_EMS SET EMS_NAME = #{cnEms_name}, IP_ADDRESS = #{ip}, PORT = #{port}, VENDOR_ID = #{vendor_id}, INSTALL_DATE = DATE_FORMAT(#{install_date},'%Y%m%d'),
UPDATE_DATE = DATE_FORMAT(#{update_date},'%Y%m%d'),
UPDATE_USER_ID = #{update_user_id} WHERE EMS_ID = #{cnEms_id}
