mapper.update:
UPDATE TB_SM_COLLECT_THD
SET 
	COLLECT_THD  = #{colThd},
	MODIFY_USER_ID = #{userId}	
WHERE 
	COLLECT_TYPE = #{colType}

mapper.select:
SELECT B.EQUIP_NAME AS SYSTEM_ID, DATA_NAME, DATA_TYPE, COLLECT_DATE, COLLECT_TYPE FROM TB_SM_COLLECT_LOG C LEFT OUTER JOIN (SELECT EQUIP_TYPE,EQUIP_NAME FROM VW_SYSTEM GROUP BY EQUIP_TYPE) AS B ON C.EQUIP_TYPE = B.EQUIP_TYPE WHERE DATA_TYPE != 3
AND DATA_TYPE = #{value}
ORDER BY COLLECT_DATE DESC
